    justify-content: center;
}

.uibox .tips {
    height: 50px;
    display: flex;
}

.uibox .tip {
    color: white;
    text-align: center;
    font-size: 18px;
    animation: fadein 2s;
    padding: 10px 10%;
    align-self: flex-end;
    width: 100%;
}

@keyframes fadein {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

