    }
    to {
        opacity: 0;
    }
}

#nick {
    background: #ededd1;
    border: 0;
    border-bottom: 6px solid #a1a18d;
    padding: 5px;
    margin: 5px;
    font-size: 25px;
    text-indent: 10px;
    color: #666;
    outline: none;
    width: 100%;
    flex: 1 1 auto;
}

#nick::placeholder {
    color: #a1a18d;
    opacity: 1; /* Firefox */
}

#play {
    flex: 0 0 130px;
}

#again,
#menu {
    flex: 1 1 auto;
}

button {
    border: none;
    border-bottom: 6px solid;
    font-size: 35px;
    letter-spacing: 1px;
    padding: 0px 15px;
    margin: 5px;
    cursor: pointer;
}

button:active {
    border-bottom: none;
    border-top: 6px solid;
}

button:focus {
    outline: none;
}

button.yellow {
    background: #eaec4b;
    border-color: #a1a130;
    color: #888a34;
}

button.green {
    background: #7fed4c;
    border-color: #56a130;
    color: #56a130;
}

button.disabled {
    background: #ebebeb;
    border-bottom: 6px solid #9c9c9c;
    color: #9c9c9c;
    cursor: default;
}

button.yellow:hover,
button.yellow:focus {
    background: #fafc5b;
}

button.green:hover,
button.green:focus {
    background: #8efc5b;
}

button.disabled:hover,
button.disabled:focus {
    background: #ebebeb;
}

button.disabled:active {
    border-top: none;
    border-bottom: 6px solid;
}

.uibox p.notsupported {
    color: rgb(177, 55, 55);
    font-size: 18px;
    text-align: center;
    padding: 0 10%;
}

